mapper.select:
select
count(*)
from user a 
where 0 = 0 
	
	 and a.email = #{email,jdbcType=VARCHAR} 
	
	
	and a.SECRETKEY = #{secretKey,jdbcType=VARCHAR} 
	
	
	and a.EXPIRED = #{expired,jdbcType=VARCHAR}

mapper.update:
		update user
	    set secretKey = #{secretKey,jdbcType=VARCHAR},
	      expired = #{expired,jdbcType=VARCHAR}
        where 
        email = #{email,jdbcType=VARCHAR}
